Phaserfroot.PluginManager.register(
  "ToggleDiagnostics_gfbjs",
  class ToggleDiagnostics_gfbjs extends Phaserfroot.Component {
    constructor( target, instanceProperties ) {
      super( {
        name: "ToggleDiagnostics_gfbjs",
        owner: target,
      } );
      this.instanceProperties = instanceProperties;
      this.scene = target.scene;
      this.game = target.scene.game;

      this.owner.once( "levelSwitch", this.destroy, this );

      // Attach custom event listeners.
      this.owner.on( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );
      this.scene.input.on( "pointerdown", this.onStageTouch2, this );


      // Initialize properties from parameters.
      this.showDiagnostics = instanceProperties[ "showDiagnostics" ];
      this.originalY = instanceProperties[ "originalY" ];


      // Boot phase.

      this.onCreate();

    }

    // BUILT-IN METHODS

    preUpdate () {

    }

    update () {
      this.EVENTS_UPDATE();

    }

    postUpdate () {

    }

    destroy () {
      this.owner.off( "levelSwitch", this.destroy, this );

      // Detach custom event listeners.
      this.owner.removeListener( this.owner.EVENTS.LEVEL_START, this.onLevelStart2, this );
      this.scene.input.off( "pointerdown", this.onStageTouch2, this );

    }

    // CUSTOM METHODS

    onCreate () {
      // Executed when this script is initially created.
      this.showDiagnostics = true;
      this.originalY = this.owner.posY;
      this.owner.setPhysics( false );
    }

    EVENTS_UPDATE () {
      // Executed every frame.
      this.owner.posY = this.math_lerp( this.owner.posY, this.originalY, 0.5 );
    }

    onLevelStart2() {

    }

    math_lerp ( a, b, t ) {
      return ( ( b - a ) * t ) + a;
    }

    reportError( message ) {
      message = "(" + this.name.replace( /_[\d]+$/, "" ) + ")" + message;
      console.trace( message );
      this.game.reportError( message, message, "SCRIPT ERROR" );
    }

    errorCheckNotNull( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    errorCheckNotNull2( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    instContains ( ins, x, y ) {
      if ( !ins || !ins.getHitbox ) return false;
      var hitbox = ins.getHitbox();
      return hitbox.contains( x, y );
    }

    onStageTouch2 ( pointer ) {
      var pointer = pointer;
      if (this.instContains( this.owner, (this.errorCheckNotNull( pointer, this.scene.input.manager.activePointer, "`Get X/Y of Pointer` block could not find a pointer named [pointer].")).x, (this.errorCheckNotNull2( pointer, this.scene.input.manager.activePointer, "`Get X/Y of Pointer` block could not find a pointer named [pointer].")).y )) {
        this.owner.posY = this.owner.posY + 8;
        this.toggleDiagnostics(  );
      }

    }

    toggleDiagnostics (  ) {
      if (this.showDiagnostics) {
        this.showDiagnostics = false;
        this.scene.broadcast( 'HIDE_DIAGNOSTICS' );
      } else {
        this.showDiagnostics = true;
        this.scene.broadcast( 'SHOW_DIAGNOSTICS' );
      }
    }

  }
);

