      this.scene = target.scene;
      this.game = target.scene.game;

      this.owner.once( "levelSwitch", this.destroy, this );

      // Attach custom event listeners.
      this.scene.input.on( "pointerdown", this.onStageInput, this );


      // Initialize properties from parameters.
      this.dx = instanceProperties[ "dx" ];
      this.dy = instanceProperties[ "dy" ];


      // Boot phase.

      this.onCreate();

    }

    // BUILT-IN METHODS

    preUpdate () {

    }

    update () {

    }

    postUpdate () {

    }

    destroy () {
      this.owner.off( "levelSwitch", this.destroy, this );

      // Detach custom event listeners.
      this.scene.input.off( "pointerdown", this.onStageInput, this );

    }

    // CUSTOM METHODS

    onCreate () {
      // Executed when this script is initially created.
      this.owner.tags.add( 'monkey' );
    }

    onStageInput () {
      // Executed whenever the stage is pressed.
      this.dx = this.scene.input.manager.mousePointer.x - this.owner.posX;
      this.dy = this.scene.input.manager.mousePointer.y - this.owner.posY;
      this.owner.bearing = Math.atan(this.dy / this.dx) / Math.PI * 180;
      if (this.dx <= 0) {
        this.owner.bearing = this.owner.bearing + 180;
      }
      this.scene.broadcast( 'shoot' );
    }

  }
);

